custom_css:
/* - - - - - - - - - - - - - - - - -

   "HyDE - Graphite Mono"
   HyDE Gallery Theme

   - - - - - - - - - - - - - - - - - */

:root {
    --customThemeColor-primary: rgba(217, 217, 217, 1);
    --customThemeColor-secondary: rgba(247, 247, 247, 1);
    --customTopBarColor: rgba(24, 24, 24, 0.8);
    --Drawer-iconColor: rgba(217, 217, 217, 0.9);
    --sheldBackgroundColor: rgba(0, 0, 0, 0);
    --custom-avatar: calc(var(--avatar-base-width) * 0.8);
    --customThemeMenuColor: var(--SmartThemeChatTintColor);
    --active: var(--customThemeColor-primary);
    --SmartThemeCheckboxTickColor: rgba(34, 34, 34, 1);
    --fullred: rgb(229, 57, 53);
    --crimson70a: rgb(229, 57, 53);
    --white30a: rgba(50, 50, 50, 0.9);
    --white50a: var(--customThemeColor-primary);
    --white70a: var(--customThemeColor-primary);
    --black30a: rgba(0, 0, 0, 0.3);
    --black50a: rgba(0, 0, 0, 0.5);
    --black70a: var(--customThemeColor-primary);
    --cobalt30a: rgba(0, 0, 0, 0.3);
    --okGreen70a: rgba(80, 217, 123, 0.8);
    --warning: var(--customThemeColor-primary);
    --golden: var(--customThemeColor-primary);
}

a { color: var(--customThemeColor-primary); }
a:hover { color: var(--customThemeColor-secondary); }
::selection { background-color: color-mix(in srgb, var(--customThemeColor-primary) 50%, transparent 50%); color: #FFF; }
h1, h2, h3 { color: var(--customThemeColor-primary); }
h3 { border-left: 5px solid var(--customThemeColor-primary); }
code { color: var(--customThemeColor-primary); }
.lastInContext { border-top: 3px dotted var(--customThemeColor-primary) !important; }
#chat .mes[is_user="true"] .name_text { color: var(--customThemeColor-primary) !important; }
#chat .mes[is_user="true"] .avatar { border: 1px solid var(--customThemeColor-primary) !important; }
.drawer-icon { color: var(--Drawer-iconColor) !important; text-shadow: 0 0 5px var(--customThemeColor-primary); }
#top-bar { background-color: var(--customTopBarColor) !important; }
#left-nav-panel, #right-nav-panel, .drawer-content { border-top: 2px solid var(--customThemeColor-primary); }
input[type="checkbox"]:checked { background-color: var(--customThemeColor-primary) !important; }
input[type="range"] { background: var(--customThemeColor-primary); }
::-webkit-scrollbar-thumb { background-color: var(--customThemeColor-primary); }
* { scrollbar-color: var(--customThemeColor-primary) transparent; }
