custom_css:
/* Hacker Terminal Theme - Custom CSS */

/* Import monospace terminal fonts */
@import url('https://fonts.googleapis.com/css2?family=Fira+Code:wght@400;500&family=JetBrains+Mono:wght@400;500&display=swap');

/* Apply terminal font to text elements only - NOT icons */
body, .mes_text, input, textarea, select, button,
p, span:not([class*="fa"]), div, h1, h2, h3, h4, h5, h6, label, a {
  font-family: 'Fira Code', 'JetBrains Mono', 'Consolas', 'Monaco', monospace !important;
}

/* Preserve Font Awesome icons */
.fa, .fas, .far, .fal, .fab, .fa-solid, .fa-regular, .fa-light, .fa-brands,
[class^="fa-"], [class*=" fa-"] {
  font-family: 'Font Awesome 6 Free', 'Font Awesome 6 Brands' !important;
}

/* Scanline effect overlay */
body::before {
  content: "";
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  background: repeating-linear-gradient(
    0deg,
    rgba(0, 0, 0, 0.15),
    rgba(0, 0, 0, 0.15) 1px,
    transparent 1px,
    transparent 2px
  );
  z-index: 9999;
}

/* Terminal-style message formatting */
.mes_text {
  line-height: 1.4 !important;
  letter-spacing: 0.5px !important;
}

/* Sharp corners for terminal look */
.mes {
  margin-bottom: 8px !important;
  border-radius: 0 !important;
  border: 1px solid rgba(0, 255, 65, 0.3) !important;
  border-left: 3px solid rgba(0, 255, 65, 0.6) !important;
}

/* Blinking cursor effect on focus */
input:focus, textarea:focus {
  box-shadow: 0 0 8px rgba(0, 255, 65, 0.5) !important;
  border-color: rgba(0, 255, 65, 0.8) !important;
  animation: terminal-glow 1s ease-in-out infinite alternate !important;
}

@keyframes terminal-glow {
  from { box-shadow: 0 0 5px rgba(0, 255, 65, 0.4); }
  to { box-shadow: 0 0 15px rgba(0, 255, 65, 0.7); }
}

/* Terminal green scrollbar */
::-webkit-scrollbar {
  width: 8px !important;
  background: rgba(0, 0, 0, 0.9) !important;
}
::-webkit-scrollbar-thumb {
  background: rgba(0, 255, 65, 0.4) !important;
  border: 1px solid rgba(0, 255, 65, 0.2) !important;
}
::-webkit-scrollbar-thumb:hover {
  background: rgba(0, 255, 65, 0.6) !important;
}

/* Green hover on buttons */
.menu_button:hover, .fa-solid:hover {
  color: rgba(0, 255, 65, 1) !important;
  text-shadow: 0 0 8px rgba(0, 255, 65, 0.8) !important;
}

/* Text glow effect */
.mes_text {
  text-shadow: 0 0 2px rgba(0, 255, 65, 0.3) !important;
}

/* No animations for focus */
body, body * {
  transition-duration: 0s !important;
}
