custom_css:
/* Verdant Theme - Custom CSS */

/* Good line spacing for readability */
.mes_text {
  line-height: 1.55 !important;
}

/* Rounded message bubbles with green glow */
.mes {
  margin-bottom: 10px !important;
  border-radius: 14px !important;
  border: 1px solid rgba(60, 180, 80, 0.3) !important;
}

/* Bold green glow on focus */
input:focus, textarea:focus {
  box-shadow: 0 0 12px rgba(80, 220, 100, 0.6) !important;
  border-color: rgba(80, 220, 100, 0.7) !important;
}

/* Green-tinted scrollbar */
::-webkit-scrollbar-thumb {
  background: rgba(60, 180, 80, 0.5) !important;
}
::-webkit-scrollbar-thumb:hover {
  background: rgba(80, 220, 100, 0.7) !important;
}

/* Green accent on buttons */
.menu_button:hover, .fa-solid:hover {
  color: rgba(100, 255, 130, 1) !important;
}

/* Reduce animation distractions */
* {
  transition-duration: 0.15s !important;
}
